import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ContextGlobal } from './utils/global.context';

const Navbar = () => {
  const { state, setTheme } = useContext(ContextGlobal);
  const { theme, isDarkMode } = state;

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
  };

  return (
    <nav className={`navbar ${theme === 'dark' ? 'dark-theme' : ''}`}>
      <ul>
      <Link to="/">
          <p className={`p ${isDarkMode ? 'dark' : 'light'}`}>Home</p>
        </Link>
        <Link to="/contact">
        <p className={`p ${isDarkMode ? 'dark' : 'light'}`}>Contacto</p>
        </Link>
        <Link to="/favs">
        <p className={`p ${isDarkMode ? 'dark' : 'light'}`}>Favoritos</p>
        </Link>
      </ul>
      <button className="theme-button" onClick={toggleTheme}>
        {theme === 'dark' ? '‚òÄÔ∏è' : 'üåë'}
      </button>
    </nav>
  );
};

export default Navbar;
