import React, { useReducer, useEffect } from 'react';
import { Link } from 'react-router-dom';

const initialState = {
  isFavorite: false,
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'SET_FAVORITE':
      return {
        ...state,
        isFavorite: action.payload,
      };
    default:
      return state;
  }
};

const Card = ({ name, username, id }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const existingFavs = await localStorage.getItem('favorites');
        if (existingFavs) {
          const favorites = JSON.parse(existingFavs);
          const isAlreadyFavorite = favorites.some((favorite) => favorite.id === id);
          dispatch({ type: 'SET_FAVORITE', payload: isAlreadyFavorite });
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [id]);

  const handleFavoriteToggle = () => {
    const cardData = {
      id,
      name,
      username,
    };

    const updateFavorites = async (favorites) => {
      await localStorage.setItem('favorites', JSON.stringify(favorites));
    };

    const updateFavoriteState = (isFavorite) => {
      dispatch({ type: 'SET_FAVORITE', payload: isFavorite });
    };

    const handleToggle = async () => {
      const existingFavs = await localStorage.getItem('favorites');
      let favorites = existingFavs ? JSON.parse(existingFavs) : [];

      const isAlreadyFavorite = favorites.some((favorite) => favorite.id === cardData.id);

      if (!isAlreadyFavorite) {
        favorites.push(cardData);
        await updateFavorites(favorites);
        updateFavoriteState(true);
      } else {
        const updatedFavorites = favorites.filter((favorite) => favorite.id !== cardData.id);
        favorites = updatedFavorites;
        await updateFavorites(favorites);
        updateFavoriteState(false);
      }
    };

    handleToggle();
  };

  return (
    <>
      <div>
        <h3>{name}</h3>
        <p>{username}</p>
        <button onClick={handleFavoriteToggle}>
          {state.isFavorite ? 'Eliminar de favoritos' : 'Agregar a favoritos'}
        </button>
        <Link to={`/dentist/${id}`}>Ver detalle</Link>
      </div>
    </>
  );
};

export default Card;
