import React, { useContext } from "react";
import { ContextGlobal } from "../path/to/ContextGlobal";

const ExampleComponent = () => {
  const { state, setTheme, setData } = useContext(ContextGlobal);

  // Acceder al estado y las funciones de actualización
  const { theme, data } = state;

  const handleThemeChange = () => {
    setTheme("dark");
  };

  const handleDataUpdate = () => {
    const newData = [...data, { id: 1, name: "New Data" }];
    setData(newData);
  };

  return (
    <div>
      <p>Theme: {theme}</p>
      <button onClick={handleThemeChange}>Change Theme</button>
      <button onClick={handleDataUpdate}>Update Data</button>
      {/* Resto del contenido del componente */}
    </div>
  );
};

export default ExampleComponent;
