import React, { createContext, useState } from 'react';

// Crea el contexto
export const MyContext = createContext();

// Crea el componente ContextProvider
const ContextProvider = ({ children }) => {
  // Define el estado y las funciones que quieres proporcionar en el contexto
  const [state, setState] = useState('');

  // Crea las funciones o métodos que deseas compartir en el contexto
  const updateState = (newValue) => {
    setState(newValue);
  };

  // Crea el objeto de contexto que contiene el estado y las funciones
  const contextValue = {
    state,
    updateState,
  };

  // Devuelve el proveedor de contexto con los hijos envueltos dentro
  return (
    <MyContext.Provider value={contextValue}>
      {children}
    </MyContext.Provider>
  );
};

export default ContextProvider;
