import React, { useReducer, useEffect } from 'react';
import { Link } from 'react-router-dom';


const initialState = {
  isFavorite: false,
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'SET_FAVORITE':
      return {
        ...state,
        isFavorite: action.payload,
      };
    default:
      return state;
  }
};

const Card = ({ name, username, id }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/users/${id}`);
        const data = await response.json();
        const isAlreadyFavorite = localStorage.getItem('favorites')?.includes(id);
        dispatch({ type: 'SET_FAVORITE', payload: isAlreadyFavorite });
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [id]);

  const handleFavoriteToggle = () => {
    // Resto del código del manejo de favoritos...
  };

  return (
    <div className="cardContainer">
      <Link to={`/dentist/${id}`} className="link">
        <img src="/img/doctor.jpg" alt="DH-logo" width={'150px'} />
        <h4>{name}</h4>
        <h5>{username}</h5>
      </Link>
      <p onClick={handleFavoriteToggle}>
        {state.isFavorite ? (
          <span>⭐️</span>
        ) : (
          <span>☆</span>
        )}
      </p>
    </div>
  );
};

export default Card;
